import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'

// Define a service using a base URL and expected endpoints
export const employeeApi = createApi({
  reducerPath: 'employeeApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'http://localhost:4006/api/' }),
  endpoints: (builder) => ({
    getEmployee: builder.query({
      providesTags: ['emp-list'],
      query: () => `employee`,
    }),
    createEmployee: builder.mutation({
      invalidatesTags: ['emp-list'],
      query: payload => ({
        url: 'employee',
        method: 'POST',
        body: payload

      })
    }),
    createEmployee: builder.mutation({
      invalidatesTags: ['emp-list'],
      query: payload => ({
        url: `employee/`,
        method: 'POST',
        body: payload

      })
    }),
    deleteEmployee:builder.mutation({
      invalidatesTags: ['emp-list'],
      query: id=>({
        url:`employee/${id}`,
        method: 'DELETE'
      })
    }),
    getEmployeeById: builder.query({
      // providesTags:['emp-list'],
      query:id=>({
        url:`employee/${id}`,
      })
    })
  }),
})

// Export hooks for usage in functional components, which are
// auto-generated based on the defined endpoints
export const { useGetEmployeeQuery, useCreateEmployeeMutation, useDeleteEmployeeMutation, useGetEmployeeByIdQuery } = employeeApi
